#include <array>

int reverseDegree(string &s) {
    // std::array<int, 26> arr;
    int degree=0;

    for(int i=0;i<s.length();i++)
    {
        degree += ( (26 - (s[i] - 'a'))* (i+1));
    }
    return degree;
}

