#include "include.hpp"

int maxProfit(vector<int>& prices) {
    int profit = 0;
    for(int l=0,r=0;r<prices.size();r++)
    {
        if(prices[l]>prices[r])
        {
            l=r;
        }
        else if(prices[l]<prices[r])
        {
            profit+=prices[r]-prices[l];
            l=r;
        }
    }
    return profit;
}

